
// create by scratch3-extension generator
const ArgumentType = Scratch.ArgumentType;
const BlockType = Scratch.BlockType;
const formatMessage = Scratch.formatMessage;
const log = Scratch.log;

const menuIconURI = "data:image/png;base64,";
const blockIconURI = null;

const n2hex =  n => {
 return ('0' + (n & 0xFF).toString(16)).slice(-2)
};

class thmprinter{
  constructor (runtime){
    this.runtime = runtime;
    // communication related
    this.comm = runtime.ioDevices.comm;
    this.session = null;
    this.runtime.registerPeripheralExtension('thmprinter', this);
    // session callbacks
    this.reporter = null;
    this.onmessage = this.onmessage.bind(this);
    this.onclose = this.onclose.bind(this);
    this.write = this.write.bind(this);
    // string op
    this.decoder = new TextDecoder();
    this.lineBuffer = '';
  }

  onclose (){
    this.session = null;
  }

  write (data, parser = null){
    if (this.session){
      return new Promise(resolve => {
        if (parser){
          this.reporter = {
            parser,
            resolve
          }
        }
        this.session.write(data);
      })
    }
  }

  onmessage (data){
    const dataStr = this.decoder.decode(data);
    this.lineBuffer += dataStr;
    if (this.lineBuffer.indexOf('\n') !== -1){
      const lines = this.lineBuffer.split('\n');
      this.lineBuffer = lines.pop();
      for (const l of lines){
        if (this.reporter){
          const {parser, resolve} = this.reporter;
          resolve(parser(l));
        };
      }
    }
  }

  scan (){
    this.comm.getDeviceList().then(result => {
        this.runtime.emit(this.runtime.constructor.PERIPHERAL_LIST_UPDATE, result);
    });
  }

  getInfo (){
    return {
      id: 'thmprinter',
      name: 'Thermal Printer',
      color1: '#0FBD8C',
      color2: '#0DA57A',
      menuIconURI: menuIconURI,
      blockIconURI: blockIconURI,
      blocks: [
        {
          opcode: 'thm_zhmode',
          blockType: BlockType.REPORTER,
          text: 'Chinese Mode [MODE]',
          arguments: {
            MODE: {
              type: ArgumentType.NUMBER,
              defaultValue: 1
            }
          },
          gen: {
            micropy: this.thm_zhmodeMpy
          }
        },
        {
          opcode: 'thm_font_size',
          blockType: BlockType.REPORTER,
          arguments: {
            SIZE: {
              type: ArgumentType.NUMBER
            }
          },
          text: 'Set Font Size [SIZE]',
          gen: {
            micropy: this.thm_font_sizeMpy
          }
        },
        {
          opcode: 'thm_send_return',
          blockType: BlockType.REPORTER,
          arguments: {
            LINE: {
              type: ArgumentType.STRING
            }
          },
          text: 'Empty Line [LINE]',
          gen: {
            micropy: this.thm_send_returnMpy
          }
        },
        {
          opcode: 'thm_print',
          blockType: BlockType.REPORTER,
          arguments: {
            TXT: {
              type: ArgumentType.STRING
            }
          },
          text: 'Print [TXT]',
          gen: {
            micropy: this.thm_printMpy
          }
        },
        {
          opcode: 'thm_set_align',
          blockType: BlockType.REPORTER,
          arguments: {
            ALI: {
              type: ArgumentType.STRING,
              menu: 'ALIMENU',
              defaultValue: '0'
            }
          },
          text: 'Set Align [ALI]',
          gen: {
            micropy: this.thm_set_alignMpy
          }
        }
      ],
      menus: {
        ALIMENU: [
          {value: '0', text: 'Left'},
          {value: '1', text: 'Mid'},
          {value: '2', text: 'Right'}
        ],
      },
    }
  }

  thm_zhmode (args, util){

    return this.write(`M0 \n`);
  }

  thm_zhmodeMpy (gen, block){
    const n = gen.valueToCode(block, 'MODE')
    if (n){
      return [`"\\x1c\\x26\\x00\\x1b\\x39\\x01"`, 0]
    } else {
      return [`"\\x1c\\x2E\\x00"`, 0]
    }
  }

  thm_font_size (args, util){
    const SIZE = args.SIZE;

    return this.write(`M0 \n`);
  }

  thm_font_sizeMpy (gen, block){
    let n = parseInt(gen.valueToCode(block, 'SIZE'))
    if (n>7) n=7;
    n = 0x11*n - 0x11
    let code = `"\\x1D\\x21\\x${n2hex(n)}"`;    
    return [code, 0]
  }

  thm_send_return (args, util){
    const LINE = args.LINE;

    return this.write(`M0 \n`);
  }

  thm_send_returnMpy (gen, block){
    let n = parseInt(gen.valueToCode(block, 'LINE'))
    let code = `"\\x1B\\x64\\x${n2hex(n)}"`
    return [code, 0]
  }

  thm_print (args, util){
    const TXT = args.TXT;

    return this.write(`M0 \n`);
  }

  thm_printMpy (gen, block){
    let t = gen.valueToCode(block, 'TXT')
    t = t.replace(/"/g, '')
    let code = `"${t}\\n"`;    
    return [code, 0]
  }

  thm_set_align (args, util){
    const ALI = args.ALI;

    return this.write(`M0 \n`);
  }

  thm_set_alignMpy (gen, block){
    let n = parseInt(gen.valueToCode(block, 'ALI'))
    let code = `"\\x1B\\x61\\x${n2hex(n)}"`
    return [code, 0]
  }

}

module.exports = thmprinter;
